import './App.css';
import Invitations from './Invitations.js';

import header from './images/header.png';
import logo from './images/logo.png';
import valentine from './images/valentine.png';
import downchevron from './images/chevron-down.png';

import FadeIn from 'react-fade-in';

import { EasybaseProvider } from 'easybase-react';
import ebconfig from './ebconfig';

function App() {

  this.state = {
  };
  this.onSubmitForm = this.onSubmitForm.bind(this);

  function lookupInvite() {
    var res = Invitations.isInvited('a');
    alert(res);
}

  return (
    <div className="App">
      <div id="slide0">
        <header>
          <FadeIn 
            delay="200"
            transitionDuration="1200"
            >
            <div>
              <img src={header} alt="header" />
            </div>
            <div>
              <img src={logo} alt="logo" />
            </div>
            <div>
              <img src={valentine} alt="valentine" />
            </div>
          </FadeIn>
        </header>
        <div className="Banner">
          <FadeIn 
            delay="1200"
            transitionDuration="1000"
            >
            <p>Youâ€™re at the right place. Keep scrolling to RSVP.</p>
          </FadeIn>
          <FadeIn 
            delay="1700"
            transitionDuration="800"
            >
            <div>
              <a href="#slide1">
                <img src={downchevron} />
              </a>
            </div>
          </FadeIn>
        </div>
      </div>

      <div id="slide1">
        <p>Please enter your name exactly as it appears in your invitation:</p>
        <form className="Form">
          <label>
            <input type="text" name="name" placeholder="e.g. Edilberto Nepomuceno" />
          </label>
          <div className="Button">
            <button onClick={lookupInvite}>Find RSVP</button>
          </div>
        </form>
      </div>

      <EasybaseProvider ebconfig={ebconfig}>
        <Invitations />
      </EasybaseProvider>

    </div>

  );
}

export default App;
