import './App.css';
// import Invitations from './Invitations.js';

import header from './images/header.png';
import logo from './images/logo.png';
import valentine from './images/valentine.png';
import downchevron from './images/chevron-down.png';

import FadeIn from 'react-fade-in';

import { EasybaseProvider, useEasybase } from 'easybase-react';
import ebconfig from './ebconfig';
import { useEffect } from 'react';

function App() {

  function lookupInvite() {
    //var res = this.props.onFormSubmit(this.state)
    var t = new Invitations();
    alert(t);

  }

  return (
    <div className="App">
      <div id="slide0">
        <header>
          <FadeIn 
            delay="200"
            transitionDuration="1200"
            >
            <div>
              <img src={header} alt="header" />
            </div>
            <div>
              <img src={logo} alt="logo" />
            </div>
            <div>
              <img src={valentine} alt="valentine" />
            </div>
          </FadeIn>
        </header>
        <div className="Banner">
          <FadeIn 
            delay="1200"
            transitionDuration="1000"
            >
            <p>You’re at the right place. Keep scrolling to RSVP.</p>
          </FadeIn>
          <FadeIn 
            delay="1700"
            transitionDuration="800"
            >
            <div>
              <a href="#slide1">
                <img src={downchevron} />
              </a>
            </div>
          </FadeIn>
        </div>
      </div>

      <div id="slide1">
        <p>Please enter your name exactly as it appears in your invitation:</p>
        <form className="Form">
          <label>
            <input type="text" name="name" placeholder="e.g. Edilberto Nepomuceno" />
          </label>
          <div className="Button">
            <button onClick={lookupInvite}>Find RSVP</button>
          </div>
        </form>
      </div>

      <EasybaseProvider ebconfig={ebconfig}>
        <Invitations />
      </EasybaseProvider>

    </div>

  );
}

function Invitations() {
    const { Frame, sync, configureFrame } = useEasybase();

    useEffect(() => {
      configureFrame({ tableName: "INVITATIONS", limit: 200 });
      sync();
    }, []);

    const onChange = (index, column, newValue) => {
      Frame(index)[column] = newValue;
      sync();
    }

    function isInvited(name) {
        return Frame().includes(name);
    }

    return (
      <div>
        {Frame().map(el => 
          <div>
            <h3>Name: {el.fullname}</h3>
            <p>Attending: {el.isattending}</p>
            <small>Email: {el.email}</small>
          </div>
        )}
      </div>
    )
  }

export default App;
