import { useEasybase } from 'easybase-react';
import { useEffect, useState } from 'react';

export function invitedHooks() {

  const [isInvited] = useState(null);
  const { Frame, sync, configureFrame } = useEasybase();

  useEffect(() => {
    configureFrame({ tableName: "INVITATIONS", limit: 200 });
    sync();
  }, []);

  const checkInvited = (name => {
    return Frame().includes(name);
  });

  return isInvited;
}
