import './App.css';
import Invitations from './Invitations.js';
import isInvited from './InvitationHooks.js';

import header from './images/header.png';
import logo from './images/logo.png';
import valentine from './images/valentine.png';
import downchevron from './images/chevron-down.png';

import FadeIn from 'react-fade-in';

import { EasybaseProvider, useEasybase } from 'easybase-react';
import ebconfig from './ebconfig';

import React from 'react';
import ReactDOM from 'react-dom';

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {showRes: false, Frame : null, sync : null};

const { Frame, sync } = useEasybase();
  };

  getInitialState = () => {
    const {showRes, value} = true;
    return { showRes: value };
  };

  showResult = () => {
    debugger;
    var methodCall = isInvited;
    var res = methodCall();
    alert(res); 
    this.setState({ showRes: true });
    alert(this.state.showRes);
  };

  render() {
    return (
      <div className="App">
        <div id="slide0">
          <header>
            <FadeIn 
              delay="200"
              transitionDuration="1200"
              >
              <div>
                <img src={header} alt="header" />
              </div>
              <div>
                <img src={logo} alt="logo" />
              </div>
              <div>
                <img src={valentine} alt="valentine" />
              </div>
            </FadeIn>
          </header>
          <div className="Banner">
            <FadeIn 
              delay="1200"
              transitionDuration="1000"
              >
              <p>You’re at the right place. Keep scrolling to RSVP.</p>
            </FadeIn>
            <FadeIn 
              delay="1700"
              transitionDuration="800"
              >
              <div>
                <a href="#slide1">
                  <img src={downchevron} />
                </a>
              </div>
            </FadeIn>
          </div>
        </div>

        <div id="slide1">
          <p>Please enter your name exactly as it appears in your invitation:</p>
          <form className="Form">
            <label>
              <input type="text" name="name" placeholder="e.g. Edilberto Nepomuceno" />
            </label>
            <div className="Button">
              <button onClick={this.showResult}>Find RSVP</button>
            </div>
          </form>
        </div>

        <div id="result">
          <EasybaseProvider ebconfig={ebconfig}>
            { this.state.showRes ? <Invitations /> : null }
          </EasybaseProvider>
        </div>

      </div>

    );
  }
}

