import './App.css';

import header from './images/header.png';
import logo from './images/logo.png';
import valentine from './images/valentine.png';
import downchevron from './images/chevron-down.png';

//import Fade from 'react-bootstrap/Fade'
import FadeIn from 'react-fade-in';

// Easybase
import { EasybaseProvider, useEasybase } from 'easybase-react';
import { useEffect } from 'react';
import ebconfig from './ebconfig';

import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <div className="App">
      <div id="slide0">
        <header>
          <FadeIn 
            delay="200"
            transitionDuration="1000"
            >
            <div>
              <img src={header} alt="header" />
            </div>
            <div>
              <img src={logo} alt="logo" />
            </div>
            <div>
              <img src={valentine} alt="valentine" />
            </div>
          </FadeIn>
        </header>
        <div className="Banner">
          <FadeIn 
            delay="1200"
            transitionDuration="800"
            >
            <p>Youâ€™re at the right place. Keep scrolling to RSVP.</p>
          </FadeIn>
          <FadeIn 
            delay="1600"
            transitionDuration="800"
            >
            <div>
              <a href="#">
                <img src={downchevron} />
              </a>
            </div>
          </FadeIn>
        </div>
      </div>/** END SLIDE0 */

      <div id="slide1">
        <p>Please enter your name exactly as it appears in your invitation:</p>
        <form className="Form">
          <label>
            <input type="text" name="name" />
          </label>
          <div className="Button">
            <input type="submit" value="Find RSVP" />
          </div>
        </form>
      </div>

      <EasybaseProvider ebconfig={ebconfig}>
        <Notes />
      </EasybaseProvider>

    </div>

  );
}

function Notes() {
  const { Frame, sync, configureFrame } = useEasybase();

  useEffect(() => {
    configureFrame({ tableName: "INVITATIONS", limit: 10 });
    sync();
  }, []);

  return (
    <div>
      {Frame().map(e => 
        <div>
          <h3>Name: {e.Name}</h3>
          <p>Attending: {e.Isattending}</p>
          <small>Email: {e.Email}</small>
        </div>
      )}
    </div>
  )
}

export default App;
