$(document).ready(function () {

    // animate.css
    $("#jumbo-text").addClass("animated fadeIn slow delay-1s");
    $("#arrow-down").addClass("animated fadeIn fast delay-2s");
    $("#arrow-down i").addClass("bounce");
    $("#header").addClass("animated fadeIn delay-2s");

    // MOBILE ONLY
    $('#hamburger').click(function(){
        if ($("#social-nav").is(":visible")) {
            $('#social-nav').hide();
            $('#navbarSupportedContent').show();
        } else {
            $('#social-nav').show();
            $('#navbarSupportedContent').hide();
        }
      });

    // Menu scroll effect
    $('header a[href*=#], #slide1 a[href*=#], #floating-footer a[href*=#], #footer a[href*=#]').each(function () {
        if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '')
            && location.hostname == this.hostname
            && this.hash.replace(/#/, '')) {
            var $targetId = $(this.hash), $targetAnchor = $('[name=' + this.hash.slice(1) + ']');
            var $target = $targetId.length ? $targetId : $targetAnchor.length ? $targetAnchor : false;
            if ($target) {
                var targetOffset = $target.offset().top;
                $(this).click(function () {
                    $("#primary-nav li a").removeClass("active");
                    $(this).addClass("active");
                    $("html, body").animate({ scrollTop: targetOffset }, 1000);

                    // Always close the hamburger menu
                    if (!$("#social-nav").is(":visible")) {
                        let navBar = $('#navbarSupportedContent');
                        if (navBar.hasClass('show')) {
                            $('#navbarSupportedContent').toggle('show');
                        }
                    }
                    return false;
                });
            }
        }
    });
});

function clearActiveClasses() {
    var selector1 = 'a[href*=#slide1]';
    var selector2 = 'a[href*=#slide2]';
    var selector3 = 'a[href*=#slide3]';

    $(selector1).removeClass('active');
    $(selector2).removeClass('active');
    $(selector3).removeClass('active');
};

$(window).scroll(function () {
    $("#floating-footer").removeClass("invisible");

    if (!$('#slide1').offset()) {
        return; // Ignore if context is invalid
    }
    var sec1 = $('#slide1').offset().top - 50;
    var sec2 = $('#slide2').offset().top - 50;
    var sec3 = $('#slide3').offset().top - 50;

    var selector1 = 'a[href*=#slide1]';
    var selector2 = 'a[href*=#slide2]';
    var selector3 = 'a[href*=#slide3]';

    var scrollPositionFromTop = $(window).scrollTop();
    if (scrollPositionFromTop >= sec1) {
        clearActiveClasses();
        $(selector1).addClass('active');
    } // do not remove this class

    if (scrollPositionFromTop >= sec2) {
        clearActiveClasses();
        $(selector2).addClass('active');
    }
    if (scrollPositionFromTop >= sec3) {
        clearActiveClasses();
        $(selector3).addClass('active');
    }


}).scroll();
